css:
@-moz-document domain("stackoverflow.com") {
body {
	padding-top: 42px;
}

.top-bar {
	border-top: 0;
	background: rgba(12,13,14,0.86);
	height: 42px;
}

.top-bar~.container {
	margin-top: 42px;
}

.top-bar .-logo,
.top-bar .navigation .-list,
.top-bar .navigation .-link,
.top-bar .secondary-nav .-list,
.top-bar .secondary-nav .-link {
	height: 42px !important;
}

.top-bar .navigation .-link,
.top-bar .secondary-nav .-link {
	line-height: 42px !important;
	color: #dedede;
}

.top-bar .-logo:hover {
	background-color: #eff0f1;
}

/* Always use the small glyph instead of full logo */
/* .top-bar .-logo {
	width: 25px;
} */

.top-bar .-logo .-img,
.top-bar .-logo .-img._glyph {
    margin-top: 0;
    
	background-image: url(https://i.imgur.com/0Df5jwN.png);
	background-size: 150px;
	background-position: left center;
	background-repeat: no-repeat;
    transition: filter cubic-bezier(.165, .84, .44, 1) .25s;
}

.top-bar .-logo:hover .-img, 
.top-bar .-logo:hover .-img._glyph {
	filter: invert(100%);
}

.top-bar .-logo .-img._glyph {
	width: 25px;
}

.top-bar .navigation .-item._current .-link{
	color: #fff;
}

.top-bar .navigation .-item._current .-link:hover,
.top-bar .navigation .-item._current .-link:focus {
	color: #242729;
}

.top-bar .searchbar input[type="text"].f-input,
.top-bar .searchbar .btn-topbar-primary,
.top-bar .searchbar .btn {
	height: 32px;
}

.top-bar .searchbar input[type="text"].f-input {
	color: #c5c9ce;
	background: #464748;
	border-color: #6a737c;
	box-shadow: none!important;
}

.top-bar .-ctas .btn-clear {
	color: #0095ff;
}

.top-bar .-ctas .btn-clear:hover, 
.top-bar .-ctas .btn-clear:focus {
	color: #0086e6;
	background-color: rgba(148, 155, 160, 0.3);
}

.top-bar .navigation .beta-badge {
	top: 26px !important;
}

.top-bar .my-profile {
	padding: 9px 10px;
	color: #848d95;
}

.top-bar .my-profile .-rep {
	color: #848d95;
}

.top-bar .my-profile:not(:hover) .-badges .badge2+.badgecount {
	color: #b4b8bc;
}
}
