css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zerohedge.com")
{
  body,
  #sidebar-right .block,
  .ad-left-container,
  #site-header,
  #sidebar-left #block-menu-secondary-links .content,
  #sidebar-left .block, #sidebar-right .block,
  #site-header .desktop-menu .expanded .menu,
  .ad-header-container
  {
    background-color: #383C4A;
  }

  #mobile-menu .mm-panels
  {
    background-color: #555A6F;
  }
  
  #page,
  h1 a, h2 a, h3 a,
  #site-header .desktop-menu > ul li a,
  .node-teaser .teaser-content .teaser-text p:last-child,
  .node-teaser .teaser-content .teaser-text p:first-child,
  h2.title a,
  .node-teaser .teaser-details .links .link-created,
  .mm-listview > li > a, .mm-listview > li > span,
  .mm-menu .mm-navbar a, .mm-menu .mm-navbar > *,
  .content p
  {
    color: #D3DAE3 !important;
  }

  #mmenu-button::before, #mmenu-button::after, #mmenu-button span
  {
    background-color: #D3DAE3;
  }
  
  .view-featured-posts .slider.slider-mobile, .view-zh-frontpage .slider.slider-mobile
  {
    background-color: #5F6475;
  }

  #site-header
  {
    box-shadow: 0 3px 10px 0 rgba(5, 2, 2, 0.42);
  }
  
  .accordion .accordion-section-title,
  a,
  .node.node-full .submitted a,
  .node.node-full .node-links .taxonomy-links .links li a,
  .content p a
  {
    color: #5294E2 !important;
  }
  
  #sidebar-right .tabBtn-hold
  {
    border-left: 10px solid #5294E2;
  }
  
  input[type="submit"],
  #sidebar-left #block-menu-secondary-links h2
  {
    background-color: #5294E2;
  }
  
  .node.node-full .node-links .taxonomy-links .links li::before
  {
    color: #7C828A;
  }
  
  .view-zh-frontpage .views-row .node.node-teaser.sticky, .view-contributing-editors-page .views-row .node.node-teaser.sticky
  {
    background-color: #6C7287
  }
  
  .view-zh-frontpage .views-row, .view-contributing-editors-page .views-row
  {
    border-bottom: 1px solid #D3DAE3;
  }
  
  .item-list ul.pager li
  {
    color: #B7BABF;
  }
}
