@-moz-document domain("ecampus.nmit.ac.nz") {
.navbar-inner {
    display: none
}
#page-header h1 {
    display: none
}
#page-header {
    display: none
}
#topadminsearchquery {
    display: none
}
#awesomebar {
    opacity: 0.0;
