css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("avherald.com") {
  body,.sitecomment, .sitetext{
    background-image:none !important;
    background-color:#222 !important;
    color:#F5F5F5 !important;
    font-size:18px !important;
    font-family: Arial !important;
  }
  
  .sitetitle,.header_avherald{
    font-family: Arial !important;
    font-size:20px;
    text-align: left;
  }
  
  *{
    font-family: Arial !important;
  }
  
  hr{
    color:#000 !important;
  }
  
  a:link{
    color:lightgreen !important;
    font-weight: 400 !important;
  }
  
  body > table:first-child > tbody > tr > td:first-child{
      text-align:left !important;
  }
  body > table:first-child > tbody > tr:nth-child(2),
  body > table:first-child > tbody > tr:nth-child(3),
  body > table:first-child > tbody > tr > td:last-child,
  body > table:first-child > tbody > tr > td:nth-child(3),
  body > table:nth-child(4) > tbody > tr > td:last-child,
  div.copyright
  {
     display:none !important;
  }
  
  /* Below for user with greasemonkey AVHerald Dark script */
  a.siteLogo, a.siteLogo:link a.siteLogo:visited{
    color:#F5F5F5 !important;
    font-size:20px;
    font-weight: 700 !important;
  }
  a.siteLogo:hover{
    color:lightgreen !important;
  }
  
  div.darkButton {
    padding:6px 12px;
    background-color:#EAEAEA;
    color:#444;
    font-size:0.95em;
    box-shadow: 0px 2px 2px #000;
    border-radius: 4px;
    display: inline-block;
  }
 
  body > table:nth-child(4) > tbody > tr > td > table:nth-child(even) td{
    background-color:#222 !important;
  }  
  body > table:nth-child(4) > tbody > tr > td > table:nth-child(odd) td{
    background-color:#222 !important;
  }  
}

@-moz-document regexp("https?://[^/]*avherald\\.com/h\\?article.*") {
  .headline_user_comments,
  form .sitecomment
  {
     display:none !important;
  }
}
