css:
@-moz-document domain("forums.overclockers.co.uk"), domain("trust.overclockers.co.uk") {

/* removes the ocuk logo and header at the top of the forum */

*#logoBlock{
    display:none;
}
#headerMover #headerProxy{
    height: 50px;
    margin-top: 20px !important;
}

/* the following few hides most of the pointless junk at the bottom of the forum on the forum home page */

#boardStats {
   display: none;   
} 

.profilePostList{
   display: none;   
} 

.membersOnline {
    display: none;   
}

.threadList {
    display: none;     
}

.staffOnline {
display: none;
}

/* removes some of the links at the top left of the forum */

.forumsTabLinks ul.blockLinksList li:nth-child(1) {
    display: none !important;
}

.forumsTabLinks ul.blockLinksList li:nth-child(2) {
    display: none !important;
}

.forumsTabLinks ul.blockLinksList li:nth-child(3) {
    display: none !important;
}

.forumsTabLinks ul.blockLinksList li:nth-child(5) {
    display: none !important;
}

/* hides the watch forum link */

.SelectionCountContainer a:nth-child(2) {
    display: none !important;
}

/* removes the quick navigation button */

.breadcrumb .jumpMenuTrigger {
        display: none;
}

/* removes the sub forum popup buttons */

.node .nodeIcon {
    margin: 3px 3px 3px 10px;
}

.Popup.subForumsPopup {
    display: none;
}

.node .nodeText {
    margin: 5px 270px 5px 56px;
}

.node .nodeLastPost {
    margin: 5px;
}

.section.threadList {
    display:none;
}

/* removes sub forum descrip */

.node .nodeDescription {
        display: none !important;
    }

  /* Move thread text left and in centre */
    
    .node .nodeText {
        margin: 12px 270px 10px 40px;
    }
  
}
