css:
@-moz-document domain("reddit.com") {     

    

body {
        background:#272b30 url(https://s27.postimg.cc/a86h0filf/skeleton7.jpg) center top fixed !important;
        background-size:cover !important;
        color:#45494d;
}

    .front-page > div.content{
        background: rgba(0,0,0, 0.5) !important;
    }

    a.title{
        font-size:14px !important;
        color:#fff !important;
    }

.tagline, .search-result-meta {
    color: #ddd !important;
    font-size: x-small;
}    
    
.tagline a, .search-result-meta a {
    color: #AAD1F8 !important;    
    
}

.entry .buttons li a {
    color: #bbb !important;
    }
    
    
.side {
    float: right;
    background-color: rgba(0,0,0,0.5) !important;;
    }    
    
.link.promotedlink.promoted, .link.promotedlink.external {
    background-color: #000 !important;
}
    
    
#header-bottom-left {
    font-size: larger;
    background: #000 !important;    
}    
    
#header-img{
    background: url(https://s18.postimg.cc/cmyt43xo9/reddit7.png) -4px 20px fixed !important;
    background-repeat: no-repeat !important;
    background-size: 150px !important;
    height: 40px;
    width: 130px !important;
}    
    
#header {
    border-bottom: 1px solid #555 !important;    
}
    
    .md-container > div.md > p {
        color:#eee !important;
    }
    
    blockquote > p    {
        color:#ccc !important;        
    }
    
    
    
    
    
    
}
