css:
@-moz-document domain("stackoverflow.com") {
.so-header {
	background-color: #242729;
}

.so-header .navigation .-link, .so-header .navigation .-item._current .-link {
	color: #eff0f1;
}

.so-header .navigation .-item._current .-link:hover {
	color: #242729;
}

.so-header .navigation .-item._current .-link {
	border-bottom: 4px solid #F48024;
}

.so-header .my-profile {
    color: #eff0f1;
}

button.btn.js-search-submit {
	background-color: #848d95;
    border-color: #2e2c2c;
    box-shadow: inset 0 1px 0 #d2d2d2;
}

.so-header._search-open input.js-search-field {
	box-shadow: inset 0 0 1px rgba(145,153,161,0.2), 0 0 0 rgba(255,255,255,0);
}

.so-header input[type="text"].f-input:focus {
	border: 1px solid #d2d2d2;
    border-radius: 2px;
}



}
