css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("java.net"),
domain("java.com") {
   
  /*text&background*/
    html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, form, footer {
        color: #aaa!important;
        background: #222!important;
    }
  
    select,
    input,
    span,
    code,
    textarea,
    pre,
    label,
    div,
    dd {
        color: #aaa!important;
    }
  
    div,
    span,
    select,
    button,
    .jvdl0v0 div,
    a.jvdla0,
    div#block-menu-menu-footer-menu,
    div.cpanel-inner,
    div.jvcs0w0 {
        background: #222!important;
    }
      
    /*links*/
    a {
        color: #7780fb!important;
    }
    a:visited {
        color: #224375!important;
    }
  
    /*highlight*/
    textarea,
    pre,
    input,
    code {
        background: #333!important;
    }
}
