css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document regexp("^data:text/html.*/devtools/codemirror/.*"),
	url-prefix("chrome://devtools/content/sourceeditor/codemirror/")
{
	.devtools-monospace {
		font-family: monospace; 
		font-size: 12pt;
	}

	.CodeMirror-code {
		font-size: 20px !important;
		line-height: 1.2;
	}
}
