css:
@namespace url(http://www.w3.org/1999/xhtml);
/*text&background*/
@-moz-document domain("oreilly.com") {


main, article,aside {
  color: #8e8e8e !important;
  background: #1e1e1e !important;
}

/*links*/

em,.article-body dl a, .article-body ol:not(.a-pagination) a, .article-body p a, .article-body ul a, .nice-underline {
  color: #0040ff !important;
  background: #1e1e1e !important;
  text-shadow: 0px 0px #1e1e1e, 0px 0px #1e1e1e, 0px 0px #1e1e1e, 0px 0px #1f1f1f !important;
}

/*highlight*/

/*textarea, pre, input, code {
  background-color: #1e1e1e !important;
}*/
/*footer image*/
img.animal-img{
  content: '';
  position: absolute;
  right: 0;
  width: 11.25rem;
  bottom: 0;
  max-height: 11.25rem;
  z-index: 3;
}
.featured-sessions {
    background-color: #1d1d1d;
    padding-top: 1.25rem;
    padding-bottom: 0;
}
.featured-sessions .section-title {
    font-size: 1.0625rem;
    text-align: center;
    margin: 0;
    border-bottom: 1px solid #ededed;
    padding-bottom: 1.25rem;
  background: #1d1d1d !important;
}

}
