css:
@-moz-document url-prefix("https://bots.discord.pw/#g="), url-prefix("https://bots.discord.pw/#l="), url-prefix("https://bots.discord.pw/#s=") {
body {
    background-color: #36393E;
}
.input,
.textarea {
    background: #36393E;
    border: 1px solid #44474C;
    color: white;
}
.button,
.pagination a {
    background: #36393E;
    color: white;
    border: #36393E;
}
.button,
.pagination a:hover {
    color: white;
}
.button {
    background: #36393E;
    color: white;
    border: #36393E;
}
.button:hover {
    color: white;
}
.table {
    background: #36393E;
    color: white;
}
.table td,
.table th {
    border: 0px solid #44474C;
}
.table tr:hover {
    background: hsla(217, 6%, 28%, 0.25);
    color: white
}
.tag.is-success {
    background: #43B581;
}
.tag.is-warning {
    background: #FAA61A;
}
.tag.is-danger {
    background: #F04747;
}
.tag.is-info {
    background: #7189CE;
}
.footer {
    background: #36393E;
    color: white;
}
.select select {
    background: #36393E;
    border: 1px solid #36393E;
    color: white;
}
}
