css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.webropolsurveys.com") {
  
  /* large fonts */ 
  table tbody td span {
      font-size: 1.7em !important;
      font-family: sans-serif !important;
      padding: 15px;
      display: block;

  }
  
  /* lighter background color */ 
  #panelBody {
    background: rgba(173, 216, 230, 0.1) !important;;
  }
  
  /* make it wide */ 
  #surveyDiv {
      width: 90vw !important; 
  }
  
  /* alternate row colors to make them readable */ 
  .quest-div table tbody tr:nth-child(even) {
      background: rgba(173, 216, 230, 0.3);
  }
  
  /* submit button should be larger */
  .clsBtn {
    padding: 50px 30%;
    font-size: 2em;
    background: green;
    letter-spacing: 1em;
    color: white;
    font-weight: 700;
    border-radius: 10px;
    margin-bottom: 0px;
  }
  
}
