css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("php.net") {

	.navbar {
			border-color: #000;
			background: #1b6f4b;
			box-shadow: 0 .25em .25em rgba(0,0,0,.1);}
	
	body { background-color:#1d1d1d; font-size: 14px; line-height: 1.5rem; } 
	
	body, input, textarea {
			font-family: "Consolas", sans-serif;		
			font-weight: 400;
			background-color:#1d1d1d; 
			font-size: 1rem; 
			line-height: 1.5rem;}
	
	#layout-content {
      color: #1d1d1d;}
	
  #layout-content {
	    border-right: 10px solid #515e92 !important;}
	
	.blurb { background-color: #FFF; }
	
	#intro p, #intro ul {
    text-shadow: 0 0px 0px rgba(0,0,0,.5);
    font-size: 1.125rem;
    color: #1d1d1d;
		font-size: 16px;
		font-weight: bold !important;
}
}
