css:
@-moz-document domain("rpgamers.net") {
    .content {
       background-color: #0f0f0f;
       color: #bbbbbb;
    }

    .panel {
        background-color: #2f2f2f;
    }

    body {
       background-color: black;
       background-image: none;
    }
    
    body center {
        color:white;
    }
    
    a {
        color: #4cace0;
    }
    
    .table {
        background: #2f2f2f none repeat scroll 0 0;
    }
   
    .table td {
        color:white;
    }
    
    .table th {
        background: #316192 none repeat scroll 0 0
        153668
    }
    
    #target {
        background-color: #111;
    }
    
    div.gameName:hover {
        background-color: #316192;
    }
    div.trackNode:hover {
        background-color: #316192;
    }
    
    div.locked {
        background-color: rgb(75, 40, 8);
    }

    
    #radio_bottompanel {
        background-image: none;
        background-color: black; 
    }
        
    .user-request {
    background-color: #349fbc;
    }
    
    .user-request:hover {
        background-color: #19b0d6;
        color : #000;
    }
    
    .table-row-highlight td:hover {
        background-color: #316192;
    }
    
}
