css:
@-moz-document domain('github.com') {
  a {
    color: #2976cc;
  }

  .link-gray-dark:hover {
    color: #2976cc !important;
  }

  table.files td.icon .octicon-file-directory {
    color: #2976cc;
  }

  .filter-item.selected {
    background-color: #2976cc;
  }

  .btn-outline {
    color: #2976cc;
  }

  .btn-outline:focus {
    border-color: #2976cc;
  }

  .btn-outline:hover,
  .btn-outline:active,
  .btn-outline.selected {
    background-color: #2976cc;
    border-color: #2976cc;
  }

  .octofication .broadcast-icon {
    color: #2976cc;
  }

  .octofication .message {
    border-color: #2976cc;
  }

  .subnav-item.selected,
  .subnav-item.selected:hover,
  .subnav-item.selected:focus {
    background-color: #2976cc;
    border-color: #2976cc;
  }

  .dropdown-item:hover,
  .dropdown-item.zeroclipboard-is-hover {
    background-color: #2976cc;
  }
  
  .gist-snippet .link-overlay .link {
    background-color: #2976cc;
  }
  
  .gist-snippet .file-box:hover .file {
    border-color: #2976cc;
  }
  
  .ajax-pagination-form .ajax-pagination-btn {
    color: #2976cc;
  }

  .numbers-summary a:hover {
      color: #2976cc;
  }

  a.branch-name {
      color: #2976cc;
  }
  
  .discussion-item-changes-marker.is-unread .discussion-item-icon {
    background-color: #2976cc;
  }

  .timeline-commits .commit-message>code a:hover {
    color: #2976cc;
  }

  .pr-toolbar .subset-files-tab {
    color: #2976cc;
  }

  .full-commit .btn-outline:not(:disabled):hover {
    background-color: inherit;
  }
}
