css:
@-moz-document domain("ruby-doc.org") {
body, p {
    background-color: #333;
    color: #ccc;
}
h1,
h2,
h3,
h4,
h5,
h6 {
   color: #ccc;
}
a, a:visited, a:link {
    color: #ff5f5f !important;
}

#documentation dt {
    color: #333;
}
#documentation dl.rdoc-list.note-list dt, 
#documentation dl.rdoc-list.label-list dt{
	color: #333;
    font-weight: normal;
}

#documentation dl.rdoc-list.note-list dt code,
#documentation dl.rdoc-list.label-list dt code
{
    color: #eee;
}

#documentation .method-heading {
/*    background-color: #444; */ 
    color: #bbb; 
}

#documentation .method-detail  {
    padding: 10px;
}

#documentation .method-detail:hover,
#documentation .method-heading:hover,
#documentation .method-detail:hover p
{
    background-color: #444;
    
}
#documentation h3.section-header {
	background-color: #aaa;   
}
#metadata h3.section-header,
#metadata .section

{
	background-color: #333;
    color: #ccc;
}
pre {
    border: 1px solid #555;
}

code {
    background-color: #222;
    padding: 2px 4px;
}
#namespace-list-section span.type {
    color: black;
    background-color: #ccc;
    font-family: sans-serif;
    font-size: 9px;
}
.associated p {
    color: #333;
    background-color: transparent;
}
.associated p code {
    color: #ccc;
}
}
