css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
#c2mp3-button-div-yt{
  margin-left: 16%;
  background-color: transparent !important;
}
.c2mp3button{
  background-color: #333 !important;
  border-color: gray !important;
  color: gray !important;
  text-shadow: none !important;
}
.c2mp3button:hover{
  background-color: #444 !important;
}
.yt-uix-button-content{
  color: black !important;
}
.c2mp3-nav{
  margin-top: 7px;
  margin-left: 8.5px; 
  border-radius: 5px !important;
  text-shadow: none !important;
}
#convert2mp3-button-3gp-youtube, #convert2mp3-button-aac-youtube, #convert2mp3-button-flac-youtube, #convert2mp3-button-m4a-youtube, #convert2mp3-button-ogg-youtube, #convert2mp3-button-wma-youtube{
  background-color: #333 !important;
  color: gray !important;
}
#convert2mp3-button-3gp-youtube:hover, #convert2mp3-button-aac-youtube:hover, #convert2mp3-button-flac-youtube:hover, #convert2mp3-button-m4a-youtube:hover, #convert2mp3-button-ogg-youtube:hover, #convert2mp3-button-wma-youtube:hover{
  background-color: #444 !important;
}
}
