css:
@-moz-document domain("osu.gatari.pw") {
/* By Andryshik345, https://osu.gatari.pw/index.php?u=1873 and vk.com/goose_senpai */
/* Default background: http://i.imgur.com/MGcpOh3.jpg */
body {
background-image: url(http://i.imgur.com/MGcpOh3.jpg) !important;
background-size: cover !important;
background-position: 50% !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
color: #fff 
}

.beatmap-info, .beatmap-favs {
 background: rgba(0,0,0,0.5) !important;
 border-radius: 5px !important;
}

td#stats-value {
color: #67D8DC !important;
text-shadow: 1px 1px 2px black, 0 0 1em #67D8DC !important;
}

a, b {
color: #67D8DC !important;
text-shadow: 1px 1px 2px black, 0 0 1em #67D8DC !important;
}

.btn {
background-color: rgba(107, 107, 107, 0.43) !important;
border-radius: 5px;
}

.panel-body {
background-image: none !important;
background-color: rgb(9,11,23) !important;
}

.panel-heading {
background-color: #090b17 !important;
}

.warning, .success {
background: none !important;
}

.alert-warning {
background: #214647 !important;
border-color: #67D8DC !important;
color: red !important;
text-shadow: 1px 1px 2px black, 0 0 1em red !important;
}

.alert.alert-up {
background: rgba(0,0,0,0.5) !important;
border-color: none !important;
border-radius: 5px;
}

.tt-menu {
background-color: rgba(0,0,0,0.5) !important;
color: #67D8DC !important;
text-shadow: 1px 1px 2px black, 0 0 1em #67D8DC !important;
}

.dropdown-menu {
background-color: rgba(0,0,0,0.5) !important;
}

.dropdown-menu>li>a {
background-color: rgba(0,0,0,0.5) !important;
display: block;
clear: both;
font-weight: 500;
line-height: 1.42857143;
color: #67D8DC !important;
white-space: nowrap;
cursor: pointer;
}

.col-lg-12 {
background: rgba(0,0,0,0.5) !important;
border-radius: 8px !important;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th
{
background-color: #26595b !important;
color: #fff !important;
}

.table>tbody>tr.warning>td, .table>tbody>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>thead>tr>td.warning, .table>thead>tr>th.warning {
background-color: #3d8082 !important;
color: #fff !important;
}

.list-group-item {
background-color: #333 !important;
color: #fff !important;
}
}
