css:
@-moz-document domain("xataka.com.mx"), domain("xataka.com") {

main
	{
		background-color: #222222;
	}
	
	.article
	{
		background-color: #333333;
		color: #ffffff;
	}

	.actualizacion
	{
		background-color: #333333;
		color: #000000;
	}

	.article-home-header a
	{
		color: #ffffff;
	}

.section-instagram-container
	{display: none !important;}

div.article-aside-content
	{display: none !important;}

section.home-article-list
	{width: 1080px;}

section.section-related
	{display: none !important;}

div.ad,
div.ad-cen
	{display: none !important;}

#div-gpt-cen
	{display: none !important;}

iv.ad-box
	{display: none !important;}

.article-club 
	{display: none !important;}

.article-espaciovisa
	{display: none !important;}

.article-social-share
	{display: none !important;}

.m-in-links
	{display: none !important;}

#recommendedPosts
	{display: none !important;}

div.cookies-overlay, #cookies-overlay
	{display: none !important;}

.foot
	{display: none !important;}

div#comments, .comment-section
	{
	background-color: #333333;
	}

.abstract-title a {
    color: #fffeff;
}

.abstract-article, .abstract-longform, .abstract-ecommerce, .abstract-respuestas, .abstract {
    background: #222;
}

h1, h2, h3, h4, h5, h6, p, pre, blockquote {
    color: #fffeff;
}

.article-header-branded-container {
    background: #333333;
}

.section-recent-aside
{
display: none !important;
}


.comment-content.blob p, .comment-content.blob ul, .comment-content.blob ol, .comment-content.blob blockquote, .comment-content.blob h2, .comment-content.blob h3, .comment-content.blob h4 {
    color: black;
}

.comment-write-form, .comment-item {
    background: #6b6868;
}

}
