css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("isocpp.org") {
  
  /*** Palette ***/
  :root {
    --default-bkgd-color: #282828;
    --bkgd-color-2: #333333;
    --default-color: #bbb;
    --color-2: #ccc;
    --border-color: #666666;
  }
  
  /***** isocpp.org *****/
  
  a:link, a:visited, a:hover, a:active {
    color: #3b97bd; /* from default #4183c4 */
  }
  
  a:hover {
    color: #5eaccd;
  }
  
  /* Search field on top right */
  #top form, #top form input {
    background-color: var(--bkgd-color-2);
  }
  
  /* below Top Region */
  #mainbar {
    background-color: var(--default-bkgd-color);
    color: var(--default-color);
  }
  
  #mainbar header h2 {
    color: var(--default-color);
  }
  
  article.intro h3 a, .home-column article h3 a, .home-column article h4 a {
    color: var(--default-color);
  }
  
  /* Working draft Github symbol */
  img[src="https://isocpp.org/images/uploads/github.png"] {
    filter: invert(82%);
  }
  
  /* Right-side Boxed-Pane */
  #mainbar .home-column article.shaded {
    background-color: var(--bkgd-color-2);
  }
  
  .hover-pop-out:hover {
    background-color: var(--bkgd-color-2);
  }
  
  /***** isocpp.org/get-started *****/
  
  /* Page heading at top left */
  .breadcrumbs {
    background-color: var(--bkgd-color-2);
  }
  
  /* Next, Back to Top */
  .pagination, .back-to-top {
    background-color: var(--bkgd-color-2);
  }
  
  /***** isocpp.org/wiki/faq *****/
  
  /* 'View' on top right */
  .wiki-page-actions {
    background-color: var(--bkgd-color-2);
  }
  
  /* Borders */
  #mainbar header {
    border-bottom-color: var(--border-color);
  }
  
  hr {
    border-top-color: var(--border-color);
  }
  
  /* Instapaper symbol */
  .save-to-reader a.icon.instapaper {
    filter: invert(80%);
  }
  
  /***** isocpp.org/wiki/faq/<sub-topic> *****/
  
  /* Sub-titles */
  #wiki-content div.prettify h3.faq-title {
    background-color: var(--bkgd-color-2);
    border-bottom-color: var(--border-color);
    opacity: 0.8;
  }
  
  .faq-title {
    border-top-width: 1px;
    border-top-color: var(--border-color);
  }
  
  /* Code in text */
  code {
    color: #0070c0; /* .kwd color - #0070c0 */ /* Purple - #7f5ecd */
  }
  
  /* Code block */
  pre.prettyprint {
    background-color: var(--bkgd-color-2);
    color: var(--default-color);
  }
  
  .str, .pln, .typ {
    color: var(--color-2);
  }
  
  .pun {
    color: var(--default-color);
  }
  
  .lit {
    color: #ac7339;
  }
  
  /* Pop-up e.g. in isocpp.org/wiki/faq/mixing-c-and-cpp#cpp-calls-c header */
  .qtip-light {
    background-color: var(--bkgd-color-2);
    color: var(--default-color);
  }
  
  /***** isocpp.org/search/google?q=<query> *****/
  
  .gsc-control-cse {
    background-color: var(--default-bkgd-color);
    border-color: var(--border-color);
  }
  
  .gsc-above-wrapper-area {
    border-bottom-color: var(--border-color);
  }
  
  .gsc-webResult.gsc-result {
    border-color: var(--border-color);
  }
  
  .gsc-control-cse .gs-snippet {
    color: var(--default-color);
  }
  
  .gsc-results .gsc-cursor-box .gsc-cursor-page {
    color: var(--default-color);
  }
  
  /***** isocpp.org/blog *****/
  
  .comment-add {
    background-color: var(--bkgd-color-2);
  }
  
  /* Post tags e.g. at isocpp.org/blog/tag/basics */
  #mainbar p.byline a.tag {
    background-color: #4183c4;
    color: #ccc;
  }
  
  #mainbar p.byline a.tag:hover {
    background-color: white;
    color: #4183c4;
  }

}
