css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {

.notification .notification_right img {
    width: 75px;
    height: 75px;
    top: 0px;
}
    
.notification .notification_inner, .notification .banner_notification_inner {
    height: 75px;
    top: 0;
    padding: 10px 10px 10px 20px;
}
    
.notification_sentence {
    padding: 2px 30px 4px 25px !important;
}
    
.ui_post_badge {
    width: 75px;
    height: 75px;
}
    
.ui_notes .ui_note .stage {
    min-height: 90px;
}
    
.ui_notes .ui_note .part_icon {
    top: 7px;
    right: 40px;
}
    
}
