css:
@-moz-document domain("twitter.com"), domain("support.twitter.com") {
.ProfileTimeline a[class*="action-profile"][class*="user-profile"] img,
div.permalink.light-inline-actions.stream-uncapped.has-replies.original-permalink-page > div.inline-reply-tweetbox.swift > form > div.tweet-content > img {
  filter: blur(0px) !important;
  -webkit-filter: blur(0px) !important;
  -moz-filter: blur(0px) !important;
  -o-filter: blur(0px) !important;
  -ms-filter: blur(0) !important;
  -ms-filter: blur(0px) !important;
}
header[role="banner"] {
  background-color: #4a9fe0 !important;
  background-image: none !important;
}
a[class*="recommend"] img {
  filter: blur(5px);
  -webkit-filter: blur(5px);
  -moz-filter: blur(5px);
  -o-filter: blur(5px);
  -ms-filter: blur(5);
  -ms-filter: blur(5px);
}
a[class*="recommend"]:hover img {
  filter: blur(0px);
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0);
  -ms-filter: blur(0px);
}
.ProfileCard-bg {
  filter: blur(10px);
  -webkit-filter: blur(10px);
  -moz-filter: blur(10px);
  -o-filter: blur(10px);
  -ms-filter: blur(10);
  -ms-filter: blur(10px);
}
.ProfileCard-bg:hover {
  filter: blur(0px);
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0);
  -ms-filter: blur(0px);
}
div[class*="poster-image"] {
  filter: blur(10px);
  -webkit-filter: blur(10px);
  -moz-filter: blur(10px);
  -o-filter: blur(10px);
  -ms-filter: blur(10);
  -ms-filter: blur(10px);
}
div[class*="poster-image"]:hover {
  filter: blur(0px);
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0);
  -ms-filter: blur(0px);
}
div.tcu-imageWrapper {
  filter: blur(10px);
  -webkit-filter: blur(10px);
  -moz-filter: blur(10px);
  -o-filter: blur(10px);
  -ms-filter: blur(10);
  -ms-filter: blur(10px);
}
