  filter: blur(0px);
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0);
  -ms-filter: blur(0px);
}
div[class*="front"] img[class*="front"] {
  display: none !important;
}
img[class*="StartCongratulation"] {
  display: none !important;
}
div[class*="stream"] img,
div[class*="stream"] video {
  filter: blur(10px);
  -webkit-filter: blur(10px);
  -moz-filter: blur(10px);
  -o-filter: blur(10px);
  -ms-filter: blur(10);
  -ms-filter: blur(10px);
}
div[class*="stream"] img:hover,
video:hover {
  filter: blur(0px);
  -webkit-filter: blur(0px);
  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0);
  -ms-filter: blur(0px);
}
video {
  filter: blur(10px);
  -webkit-filter: blur(10px);
  -moz-filter: blur(10px);
  -o-filter: blur(10px);
  -ms-filter: blur(10);
  -ms-filter: blur(10px);
}
