  -moz-filter: blur(0px);
  -o-filter: blur(0px);
  -ms-filter: blur(0);
  -ms-filter: blur(0px);
}
div[class*="stream"] img[class*="profile"] {
  filter: blur(4px);
  -webkit-filter: blur(4px);
  -moz-filter: blur(4px);
  -o-filter: blur(4px);
  -ms-filter: blur(4);
  -ms-filter: blur(4px);
}
