css:
@-moz-document url-prefix("http://www.nicovideo.jp/my") {
body{
    background: #2c3e50;
}

#nicorepo .articleBody {
    background-color: #2c3e50;
    color: aliceblue;
}
a, a:link {
    color: gainsboro;
}

#nicorepo .articleBody .log-target-info a, #nicorepo .articleBody .log-target-info a:link, #mylist .articleBody h5 a, #mylist .articleBody h5 a:link, #video .articleBody h5 a, #video .articleBody h5 a:link, #history .articleBody h5 a, #history .articleBody h5 a:link {
    color: palegreen;
 }

.userDetail {
     background: lightslategray;
 }

.userDetail a, a:link {
    color: black;
}

.sidebar a,a:link{
    color: black;
}

.articleBody a:link{
    color: yellow;
}

.log-target-info a:visited{
    color: red !important;
}

.miniSearchWrap {
    display: none;
}
.wrapper .content {
    background: #2c3e50;
}
}
