css:
@-moz-document domain("mightytext.net") {
.adContainer,
    .app-search-container,
    #heart-mt-button,
	#ad-block-holder {
        visibility: hidden;
        display: none;
        position: absolute;
    }

    #singleMessageContainer,
    #singleMessageContainer .messageThread {
        width: 600px;
    }

    #pinnedContent {
        top: 0px;
    }

    #sideNavigation {
        padding-top: 0px;
    }

    #messages-view-dropdown-selector,
    #singleMessageContainer .save-draft-button-holder,
    #singleMessageContainer .addTemplateIcon {
        background-color: #3d4142;
        border-color: #4a5363;
    }

    .fa-th-list:before,
    .fa-caret-down:before,
    .fa-file-text:before,
    .fa-angle-down:before,
    .fa-save:before,
    .fa-floppy-o:before {
        color: #9a9a9a;
    }

    .thread-header-dropdown-icon:before {
        color: #ffffff;
    }

    body,
    #pinnedContent,
    #singleMessageContainer .messageThread,
    .textResponse,
    #singleMessageContainer .responseArea,
    #threadListHolder {
        background-color: #2b3542;
    }

    .progress {
        background-image: linear-gradient(to bottom, #9a9a9a, #979797);
    }

    .responseArea,
    #navBarTabs .thread-tab,
    #navBarTabs > .active > a,
    #navBarTabs > .active > a:hover {
        border-color: #2b3542;
    }

    .threadNameOrNumber {
        color: #e8e8e8;
    }

    li.active a .threadNameOrNumber {
        color: #3d4142;
    }

    .textResponse {
        border-color: #2b3542;
        color: #d4d6d9;
        box-shadow: inset 0px 0px 4px 1px #1d1d1d;
    }
}
