/* i really want this to be global */

.pp-top-toolbar, .pp-top-toolbar-button  {
 	background-color: #246b7f !important;  
    background-image: none !important;
}

.x-btn-default-toolbar-small, .x-btn-default-small {
    background-image: none !important;
    background-color: lightgrey;
    border-radius: 0px  !important;
}

.pp-grid-first-item .pp-grid-content {
    border-radius: 0px 0px 0px 0px;
}

.pp-grid-last-item .pp-grid-content, .pp-grid-last-item .pp-grid-bottom-box {
    border-style: solid;
    border-radius: 0px 0px 0px 0px;
}

.pp-grid-selected {
    background-color: #FFFFCC;
}

#intercom-container .intercom-messenger-frame {
    border-radius: 0px !important;
}
