css:
@-moz-document domain("dust2.dk") {
body {
    background-color: #444  !important;
    background-image: none !important;
}
.profilebar {
    background-color: #333  !important;
}
.logoCon {
    max-height:0 !important;
}
.logoCenterer {
    position: absolute;
    top: -30px;
    width: 70px !important;
    height: 65px;
    background: url(http://havkajakbornholm.dk/media/1102/eb.png);
    background-position: bottom;
    background-repeat: no-repeat;
    background-size: contain;    
}
aside h1 {
    color: #555 !important;
    margin-bottom: 10px !important;
}
.vspace {
    margin-bottom: 10px !important;
}
.logoCenterer img,
.widebar aside.vspace:first-child,
.contentBox .vspacer:first-child,
footer,
.banner {
    display: none;
    visibility: hidden;
}
}
