css:
@-moz-document domain("www.youtube.com") {
.watch-stage-mode .html5-video-player{
	padding-bottom:56.25%;
    height:0;
}
.watch-stage-mode .html5-video-container{
	width:100%;
    height:100%;
    position:absolute;
    max-height: calc(100vh - 50px);
}

.watch-stage-mode .html5-video-player .video-click-tracking, .watch-stage-mode .html5-video-player .video-stream{
    max-height: 100%;
}

.watch-stage-mode #theater-background{
	background-color:transparent !important;
}

@media screen and (min-width: 1020px) {
    .watch-stage-mode .player-width {
        width: 100%;
        max-width: 1066px;
        margin: 0 auto;
        left: 50%;
        transform: translateX(-50%);
    }

    .watch-stage-mode .player-height {
        height: 599px;
    }
    
    .watch-stage-mode #player-playlist .watch-playlist {
		top: 240px;
    }
}

@media screen and (min-width: 1294px){
    .watch-stage-mode .player-width {
		max-width: 1280px;
    }
    
    .watch-stage-mode .player-height {
        height: 720px;
    }
    
    .watch-stage-mode #player-playlist .watch-playlist {
		top: 360px;
    }
}

@media screen and (min-height: 870px) and (min-width: 1720px) {
    .watch-stage-mode .player-width {
        max-width: 1706px;
    }

    .watch-stage-mode .player-height {
        height: 960px;
    }
    
    .watch-stage-mode #player-playlist .watch-playlist {
		top: 600px;
    }
}
}
