css:
@-moz-document domain("www.twitch.tv") {
.ember-chat .chat-messages {
    top: 0px;
    bottom: 114px;
    background-color: #181819;
}

body {
    color: #ffffff;
    font-family: Helvetica Neue,Helvetica,sans-serif;
    font-size: 1.2rem;
    line-height: 2rem;
}

.ember-view.ember-chat.roomMode {
    background: #222223!important;
}

.ember-chat .chat-interface .textarea-contain textarea {
    padding-right: 50px;
    background: #222222;
}

.ember-chat .chat-header {
    right: 0px;
    left: 0px;
    width: 100%;
    z-index: 4;
    background: #222222;
}

.sticky-message--pinned-cheers {
    background: #1b1a1a;
    border-bottom: .1rem solid #dad8de;
    margin: 0 .1rem;
    transition: all .25s ease;
}
}
