css:
/* i really want this to be global */
body {
	min-width: 900px;
}

.featured__inner-wrap {
	padding: 0;
}

.sidebar {
  min-width: 0;
}

.page__inner-wrap > .widget-container {
  display: block;
}

.widget-container > div:not(:first-child) {
  display: block;
  border: 0;
  padding: 0;
  margin: 0;
}

.page__inner-wrap, nav, .footer__inner-wrap, .featured__inner-wrap, .offer__inner-wrap {
	min-width: 850px;
	max-width: 1000px;
}
