css:
@-moz-document url-prefix("https://tweetdeck.twitter.com/"){
  .os-windows {
    /* your fonts */
    font-family: "Noto Sans CJK JP Regular", arial, sans-serif;
    font-feature-settings: "palt";
  }
  .tweet {
    line-height: 1em;
  }
  .tweet-text {
    padding: 0.2rem 0;
  }
  .is-wide-columns .media-size-small {
    height: 50px;
  }
  .is-wide-columns .media-size-medium {
    height: 75px;
  }
  .tweet-actions .icon, .tweet-actions .Icon {
    font-size: 0.7rem;
    height: 10px;
    line-height: 10px;
  }
}
