@-moz-document domain("youpouch.com") {


body {
    background: url('https://s20.postimg.cc/pcv3v9ju5/perfume_cling_cling.jpg') center top fixed !important;
/*    background: url('https://s20.postimg.cc/gke57kyp9/perfume_band1.jpg') center top fixed !important;         */
    background-size:cover !important;
    background-repeat:no-repeat;
}

#header {
    background: none !important;
}


#container > div#header > div#header-main{
    margin-top:200px !important;
    background:rgba(255,255,255,0.5) !important;
}


#div-gpt-ad-header{
    display:none !important;
    }

    
