/* i really want this to be global */

/* Voating Arrows */
.commentarea .thing .arrow{
    margin: 5px 0;
}

#siteTable .arrow.up, .thing .arrow.up, .res-nightmode .arrow.up:hover{
    background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) 0px -12px !important;
}

#siteTable .arrow.upmod, .thing .arrow.upmod{
    background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) -16px -12px !important;
}

#siteTable .arrow.down, .thing .arrow.down, .res-nightmode .arrow.down:hover{
    background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) 0px -28px !important;
}

#siteTable .arrow.downmod, .thing .arrow.downmod{
    background: url(https://imgoat.com/uploads/9f0f895fb9/7324.png) -16px -28px !important;
}

/* Voating Colors */

.midcol .score.likes { color: #4aabe7; } 
.midcol .score.dislikes { color: #8870ff; }
