/*Font_Colour White*/
#find input,.content,#content a,h2,h3 {
    color: #c7c7c7 !important;
}

/*Font_Colour Grey*/
.rec-component table tbody tr td,.github-bugs tbody tr td,.features li,#inshort,.target,#header a:hover,#enhancements{
    color: #828282 !important;
}

/*Header*/
#header,#footer,#header[fixed],#content {
    background: #000 !important;
}

/*Background*/
#footer table tr:last-child,#content {
    background-color: #0e0e0e !important;
}

/*Column 1*/
#contact_us>div, #join_us>div,.rec-box, .rec-component,.github-bugs>tbody>tr:nth-child(odd) {
    background-color: #151414 !important;
}

/*Column 2*/
.github-bugs>tbody>tr:nth-child(even) {
    background: #191919 !important;
}

/*Input Black*/
#find input{
    background-color: #000 !important;
}

/*box-shadow Grey*/
#header {
    box-shadow: 0 3px 3px 0 rgb(56, 56, 56) !important;
}

/*Boarder*/
#find input,table,#header td,.faq>li,.box,.nav-primary,#header td[type=search] input,hr {
    border-color: #383838 !important;
}

}

@-moz-document url-prefix("http://add0n.com/stylus.html") {
/*Background Body = Header*/
#top {
    background: #000 !important;
}
}
