@namespace url(http://www.w3.org/1999/xhtml);
/*Carla Familiar*/  

/* i really want this to be global */
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("https://s-media-cache-ak0.pinimg.com/originals/cf/c8/9f/cfc89f3034a64adfcf2eea8913c2f1ed.jpg") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("https://s-media-cache-ak0.pinimg.com/originals/cf/c8/9f/cfc89f3034a64adfcf2eea8913c2f1ed.jpg") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

    /*Infos*/

  #pet-info-name:before {
    content : "Carla Tsukinami" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Carla ha comido tu jamon y tu sensual sangre!" ;
  }
  
  
  #pet-info-racegender:before {
    content : "Vampiro" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  
  
  img[src*="/static/img/f.png"] {
    background : url("http://www.eldarya.fr/static/img/m.png") !important ;
    padding-left : 14px !important ;
    padding-top : 15px !important ;
    width : 0 !important ;
    height : 0 !important ; 
    margin-bottom : 5px !important ;
  }
 
 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("https://i.paigeeworld.com/user-media/1432598400000/54e3ccaa5b09e8561cba50cd_5564e0c8ba32136c0bc1d5c3_320.jpg") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  
  #pet-info-food:before {
    content : "Sangre de guardiana y jamon" !important ;
    font-size : 16px !important ;
  }
  
  #pet-info-food > span {font-size : 16px !important ;}

  /*Carla sur la page du profil*/

  #player-display-pet {
    background : url("https://s-media-cache-ak0.pinimg.com/originals/af/f9/98/aff99840ba9d01c442ad660e281836d4.jpg") no-repeat ;
    background-size : auto 100% ;
    height : 560px !important ;
    width : 350px !important ;
  }

  #player-display-pet > img {display : none !important ;}
