css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("coliru.stacked-crooked.com") {
    div.hbox.f0 {
        background-color: #3f3f3f !important;
        color: #aaaaaa !important;
    }
    .link.f0.vbox,
    .link.f1,
    a[id="link feedbackButton"] {
        color: #aaaaaa;
    }
    .ace-tm {
        background-color: #000000;
        color: rgb(150, 150, 150);
    }
    .coliru-btn {
        background-color: #333333;
        background-image: none;
        color: #aaaaaa;
    }
    .coliru-btn:hover {
        background-color: #666;
        background-image: none;
        color: #ccc;
    }
    .coliru-select-compiler select {
        background-color: #333333;
        background-image: none;
    }
    .ace-tm .ace_cursor {
        color: #00ff00;
    }
    .ace-tm .ace_gutter-active-line {
        background-color: rgba(0, 0, 0, 0.3);
        color: rgb(255, 255, 0);
    }
    .ace-tm .ace_fold {
        background-color: black;
    }
    .ace-tm .ace_marker-layer .ace_selection {
        background: rgba(255, 0, 0, 0.2);
    }
    .ace-tm .ace_marker-layer .ace_active-line {
        background-color: rgba(255, 255, 255, 0.05);
    }
    /* Error symbol */
    
    .ace_gutter-cell.ace_error {}
    /* Line No.s */
    
    .ace-tm .ace_gutter {
        background-color: #333333;
        color: rgb(150, 150, 150);
    }
    .ace-tm .ace_indent-guide {
        background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mP8bwwAAjcBNBRvLA0AAAAASUVORK5CYII=") right repeat-y;
    }
    .ace-tm .ace_storage,
    .ace-tm .ace_keyword {
        color: rgb(255, 0, 0);
    }
    .ace-tm .ace_storage,
    .ace-tm .ace_control {
        color: rgb(30, 144, 255);
    }
    .ace-tm .ace_storage,
    .ace-tm .ace_modifier {
        color: rgb(255, 127, 80);
    }
    .ace-tm .ace_constant {
        color: rgb(199, 21, 133);
    }
    .ace-tm .ace_type {
        color: rgb(255, 255, 0);
    }
    .ace_identifier {
        color: rgb(0, 255, 0);
    }
    .ace-tm .ace_string {
        color: rgb(120, 120, 120);
    }
    .ace-tm .ace_storage.ace_type + .ace_identifier,
    .ace-tm .ace_identifier + .ace_identifier,
    .ace-tm .ace_storage.ace_type + .ace_keyword.ace_operator + .ace_identifier {
        color: rgb(255, 255, 255);
    }
    .ace-tm .ace_constant.ace_numeric {
        color: rgb(255, 105, 180);
    }
    .ace-tm .ace_comment {
        color: rgb(0, 255, 255);
    }
    .ace-tm .ace_variable {
        color: rgb(240, 240, 240);
    }
    .ace-tm .ace_keyword.ace_operator {
        color: rgb(150, 150, 150);
    }
    .ace-tm .ace_support.ace_function {
        color: #66d9ef;
    }
    #output {
        background-color: #333333;
    }
    pre.f0 {
        color: #aaaaaa;
    }
    #footer {
        background-color: #282828;
        color: #ccc;
    }
}
