css:
@-moz-document domain("www.osnews.com") {
html  {
        font-family: Arial;
        font-size: large !important;
      }

body   { 
          width: 98% !important; 
          min-width: 600px;
          font-size: 100% !important;
          background-color: white;
       }

div#side, div#footer, div.newsfooter1   { display: none !important; }
iframe      { display: none !important; }

div#main    { width: 100% !important;  }
div.newscontent1 { width: 100% !important; padding-left: 0; }


div#main > div.item2 + div { display: none !important;  }
div#main > div.item + div { display: none !important; }

div.item, div.item2       { border: 0.3em solid silver; border-radius: 1em; padding: 1em; margin-bottom: 1em; background-color: white; }


img[title=Icon], img.timg          { display: none !important; }


div.cquote > p:first-child:before {
    content: "\201C";
    font-size: 2em;
    line-height: 0.1em;
    margin-right: 0.25em;
    vertical-align: -0.3em;  
}


.cquote   { background-image: none; }


div.cquote      { width: 100% !important; margin: 0; padding: 0;  }
div.cquote > p  { margin: 0.5em 1em; }

div.newstitle1     { font-size: 150% !important; padding: 0 !important; margin: 0 !important; }


div > br                           { display: none; }
}
