css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("materialui.co") {
    html, body {
        background: #111!important;
        color: #ccc!important;
    }
.sidebar {
background: #111!important;
}
    .content {
        background-color: #111!important;
    }
    .container {
        background-color: #111!important;
    }
    .devs {
    background-color: #111!important;
        color: #ccc!important;
    }
    .header.half {
        background: #222!important;
    }
    .s_nav .s_nav_others,
    .s_nav .s_nav_tools {
        border-bottom: 1px solid #232121!important;
        background: #222!important;
    }
    .nav_wrap.half .sub_nav {
    display: none;
    position: absolute;
    left: -10px;
    background: #111!important;
    z-index: 3;
    -moz-box-shadow: 0 0 5px 0 rgba(0,0,0,.3);
    -webkit-box-shadow: 0 0 5px 0 rgba(0,0,0,.3);
    box-shadow: 0 0 5px 0 rgba(0,0,0,.3);
    }
    .nav_wrap.half .sub_nav > li .sn_settings,
    .nav_wrap.half .sub_nav > li > a, .s_nav .s_nav_list > li > a, button {
        color: #aaa!important;
    }
    li > a {
        background-color: #111!important;
    }
    li > a:hover {
        background-color: #444!important;
    }
}
