css:
@-moz-document domain(twitter.com) {

* {
    border-radius: 0 !important;
}
    
    .front-image{
        display:none !important;
    }
    
body {
    background: #090909 !important;
}
[dir="ltr"].logged-in{
    background-position: top !important;
    background-image: url(https://pbs.twimg.com/media/C7rAxFEWkAAKZvl.jpg)!important;
    background-size: cover !important;
    background-attachment: fixed !important;
}
    
.logged-out {
    background-position: top !important;
    background-image: url(https://pbs.twimg.com/media/C7q99LcVAAAsQ_j.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}    
    
    
.ProfilePage .AppContent {
    background-position: top !important;
    background-image: url(https://pbs.twimg.com/media/C7q99LcVAAAsQ_j.jpg) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}

.topbar > div.global-nav > div.global-nav-inner{
    background: rgba(255,255,255,0.7) !important;
    }
    
    .PageDownload-info{
        background: rgba(255,255,255,0.7) !important;
        padding:10px !important;
        margin-top:210px !important;
        border-radius:3px !important;
    }

    .stream-item{
        background: rgba(255,255,255,0.9) !important;
    }
    
.dashboard.dashboard-left > div.Trends.module.trends{
        background: rgba(255,255,255,0.9) !important;    
    }    

    .timeline-tweet-box > div.home-tweet-box.tweet-box.component.tweet-user{
        background: rgba(255,255,255,0.5) !important;            
        
    }
    
#tweet-box-home-timeline.tweet-box.rich-editor.is-showPlaceholder{
    background: rgba(255,255,255,0.5) !important;   
    color:#333 !important;
    }    
    
.tweet-content > div.RichEditor{
    background: rgba(255,255,255,0.5) !important;   
    color:#333 !important;    
    }    
    
.dashboard-right{
        display:none !important;
    }    
    
#page-outer > div#page-container.AppContent.wrapper.wrapper-download > div.PageDownload > div.PageDownload-device > img.PageDownload-deviceImage{
    display:none !important;
    }    
    
#page-outer > div#page-container.AppContent.wrapper.wrapper-download > div.PageDownload > div.PageDownload-info{
    margin-top:190px !important;
    margin-left:0px;
    }   
    
#page-outer > div#page-container.AppContent.wrapper.wrapper-download > div.PageDownload{
    margin-left:-40px;
    }     
    
.logged-out > div#doc.route-download > div#page-outer > div#page-container.AppContent.wrapper.wrapper-download{
    padding-left:0px !important;    
    }    
    
#doc > div#page-outer > div#page-container.AppContent.wrapper-search > div.SearchNavigation > div{
    background:rgba(255,255,255,0.7) !important;
        
    }    
    
#page-container.AppContent > div.AppContainer > div.AppContent-main.content-main.u-cf > div.Grid.Grid--withGutter > div.Grid-cell.u-size1of3.u-lg-size1of4 > div > div.Grid-cell{
     background:rgba(255,255,255,0.7) !important;   
    }    
    
#page-outer > div#page-container.AppContent > div.ProfileCanopy.ProfileCanopy--withNav.js-variableHeightTopBar > div.ProfileCanopy-inner > div{
    background:rgba(255,255,255,0.7) !important;    
    }    
    
.ProfileNav > ul.ProfileNav-list > li.ProfileNav-item.ProfileNav-item--userActions.u-floatRight.u-textRight.with-rightCaret{
    background:transparent !important;
    }    
    
.MoveableModule > div.SidebarCommonModules > div{
    background:rgba(255,255,255,0.8) !important;  
    }

.SidebarCommonModules > div{
    background:rgba(255,255,255,0.7) !important;        
    }    
    
a:link{
    color: #ff7901 !important;
}


    #global-new-tweet-button{
        background:#ff7901 !important;
    }
    
    
    
    .Avatar--circle{
        filter: grayscale(1) !important;
    }
    
.tweet-text > a > b > strong{
    color:#ff7901 !important;
    }    
    
.fullname{
    color:#ff7901 !important;        
}    
    
    
    
    
    
    
    
    
    
    
    
}
