css:
@-moz-document domain("www.radianttranslations.com") {
  html,body,.entry-wrap,
.x-boxed-layout-active .site{
    margin:0 auto!important;padding:0 !important;
    background-image:none !important;
    background-color:#333 !important;
    color:#eee !important;
    font-size:1.2em;
}
.entry-content,p{
    font-family:sans-serif,serif !important;
font-size:20px !important;
}
.masthead .x-logobar,.masthead .x-navbar-wrap,
.code-block.code-block-6.ai-viewport-1.ai-viewport-2,.code-block.code-block-7.ai-viewport-1,.x-sidebar.right,footer.x-colophon.top,footer.x-colophon.bottom{
display:none !important;
}
.x-breadcrumb-wrap{
border-bottom:0 !important;
    box-shadow:none !important;
}
.x-breadcrumb-wrap a, .x-breadcrumbs .delimiter{color:#bababa !important;}
.x-breadcrumbs .current{ color:#eee !important}
.x-container.offset{margin:0 auto !important;}
.x-main.left{width:98% !important; margin:0 auto !important;}
.x-boxed-layout-active .entry-wrap{
border:none !important;
    padding:20px !important;
}
hr{
    border-top:1px solid #444 !important;
}
.hentry{
margin-top:0 !important;
}
h1,h2,h3,h4,h5,h6{ color: #bbb !important;}
#comments .comment{ background-color:#333 !important; color: #eee !important;}
}
