css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
* Author: Ew0345
* Version: 1.3
* Note: Updated now that I have a better understanding of CSS.
*/
@-moz-document domain("manga0205.wordpress.com") {
  body {
    background-color: #202020;
    color: #eee;
  }
  .hentry {
    background-color: #202020;
    border-color: #1f1f1f !important;
    border-width: thin;
    border-style: ridge !important;
  }
  .entry-content {
    font-family: 'Open Sans', sans-serif;
    font-size: 16px;
    line-height: 1.6;
    color: #eee !important;
    background-color: #202020 !important;
  }
  div.menu a:link,
  div.menu a:visited {
    color: #808080 !important;
    font-size: 14px;
  }
  div.menu ul li.current_page_item a:link,
  div.menu ul li.current_page_item a:visited {
    color: #a0a0a0 !important;
  }
  div.menu a:hover,
  div.menu ul li.current_page_item a:hover {
    color: #a0a0a0 !important;
    text-decoration: underline !important;
  }
  a:visited {
    color: inherit;
  }
  div.site-header-wrapper a:link,
  div.site-header-wrapper a:visited {
    color: #fff !important;
    font-size: 13px;
  }
  div.site-header-wreapper a:active {
    color: purple !important;
  }
  div#actionbar span {
    color: black !important;
  }
  div#comments {
    font-size: 14px !important;
    line-height: 1.3 !important;
  }
  .field {
    color: #eee !important;
    background-color: #232323 !important;
    border-color: #353535 !important;
  }
  .submit {
    background-color: #303030 !important;
  }
}
