css:
@-moz-document domain("www.leagueplusplus.net") {
#elLogo img {
    content: url("https://s.put.re/pWaRaQA.png");
}
#ipsLayout_header header {
    padding: 5px 0 125px 0;
}
#ipsLayout_header header .ipsLayout_container {
    padding-top: 25px;
}
body {
    background-image: url("https://s.put.re/FgVP32A.png");
}
#ipsLayout_sidebar {
    min-width: 250px;
    max-width: 315px;
    padding: 10px 0 15px 0;
    overflow: hidden;
    border-radius: 0;
}
html[dir="ltr"] #elUserNav {
    right: 20px;
}
#elUserNav {
    background-color: rgba(213, 216, 219, 0.08);
    padding-left: 10px;
    padding-right: 10px;
    padding-bottom: 2px;
}
.ipsNavBar_primary,
.ipsTabs,
.ipsType_sectionTitle,
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsItemStatus.ipsItemStatus_large {
    background: #0d0c14!important;
}
}
