css:
@-moz-document domain(www.dailywire.com) {

html {
    background-color: #252525;
    color: white;
}

a {color: white;}

#main {
    background-color: #252525;
    color: white;
}

#header {
    background-color: #252525;
    color: white;
}

#header #site-name a {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}

.article-feature-text {
    background-color: #252525;
    color: white;
}

.advertisements-toggle button {background-color: #252525;}

#main-menu-toggle span,
#main-menu-toggle span:after,
#main-menu-toggle span:before {
	background-color: White;
}

#main-menu .menu:before {
    background-color: #252525;
    color: white;
}

.bottom-right #outbrain_widget_0 .ob-widget-header,
.right #block-dailywire-parsely-hotwire h2,
.right #outbrain_widget_1 .ob-widget-header {
    background-color: #252525;
}

.view-podcast-teaser-blocks {
    background-color: #1D1D1D;
    color: white;
}

.view-content {
    background-color: #252525;
    color: white;
}

.field-addthis-compact {
    background-color: #252525;
    color: white;  
}

.region-footer-1 {
    background-color: #252525;
    color: white;
}

.jumbotron.podcast {
    background-color: #252525;
    padding-top: 1em;
}}
