css:
@-moz-document domain("onedrive.live.com") {
body {
	background-color: #b3a58c;
	background-image: url("https://www.transparenttextures.com/patterns/diamond-eyes.png");
}

a.LeftNav-groupLink.LeftNav-is-active>span:after,
html[dir=ltr] a.LeftNav-link.LeftNav-is-active>span:after,
html[dir=ltr] a.LeftNav-subLink.LeftNav-is-active>span:after,
html[dir=ltr] a.LeftNav-groupLink>.LeftNav-fadient:after,
html[dir=ltr] a.LeftNav-link>.LeftNav-fadient:after,
html[dir=ltr] a.LeftNav-subLink>.LeftNav-fadient:after,
html[dir=ltr] a.LeftNav-groupLink:hover:not(.LeftNav-is-active)>span:after,
html[dir=ltr] a.LeftNav-link:hover:not(.LeftNav-is-active)>span:after,
html[dir=ltr] a.LeftNav-subLink:hover:not(.LeftNav-is-active)>span:after {
	background-image: none;
}

.od-BasePage-leftNav {
	background-color: #715c4e;
	background-image: url("https://www.transparenttextures.com/patterns/rough-cloth-light.png");
}

a {
    color: #a9c8e0;
}

.LeftNav-linkGroupHeader,
a.LeftNav-groupLink,
a.LeftNav-link,
a.LeftNav-subLink {
	color: #fafafa;
	font-family: "Segoe UI",Tahoma,Arial,sans-serif;
}

a.LeftNav-groupLink.LeftNav-is-active,
a.LeftNav-link.LeftNav-is-active,
a.LeftNav-subLink.LeftNav-is-active {
	background-color: #b3a58c;
	color: #333;
}

a.LeftNav-groupLink:hover:not(.LeftNav-is-active),
a.LeftNav-link:hover:not(.LeftNav-is-active),
a.LeftNav-subLink:hover:not(.LeftNav-is-active) {
	background-color: #d8cfbf;
}

html[dir=ltr] a.LeftNav-groupLink.LeftNav-is-active, html[dir=ltr] a.LeftNav-link.LeftNav-is-active, html[dir=ltr] a.LeftNav-subLink.LeftNav-is-active
{
	border-left:2px solid #a9c8e0;
}
}
