css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("toppoint.de") {
  @media only screen and (min-width:801px){
    /* page left padding */
    #dokuwiki__site > .site {
      padding-left: 20px;
    }
    /* smaller nav */
    .showSidebar #dokuwiki__content > .pad {
      margin-left: 14.5em;
    }
    .dokuwiki .aside li{
        margin-left: 1em;
    }
    .dokuwiki .aside ul ul li{
        margin-left: .5em;
    }
    .dokuwiki .aside ul, .dokuwiki .aside ol {
        padding-left: 0;
    }
    /* top bar less space */
    #dokuwiki__header {
      padding:0;
      padding-top: 8px;
    }
    #dokuwiki__usertools {
      position: static;
      margin-bottom: 5px;
    }
  }
  /* active link black */
  .dokuwiki .aside .curid > a{
    color:black;
  }
  /* disable home link */
  .content > div:nth-child(1) > p:nth-child(1){
    display:none;
  }
  /* disable last link (would be "sidebar") */
  .indexmenu_nojs > ul li:last-child{
    display: none;
  }
}
