css:
@-moz-document domain("app.twitch.tv") {
/* change background-colors */
::-webkit-scrollbar-track,
*[style*="background: rgb(32, 28, 43)"],
*[style*="background: rgba(32, 28, 43"],
*[style*="background-color: rgb(32, 28, 43)"],
*[style*="background-color: rgba(32, 28, 43"]
{
	background-color: rgb(32,32,32) !important;
}

*[style*="background: rgb(23, 20, 31)"],
*[style*="background: rgba(23, 20, 31"],
*[style*="background-color: rgb(23, 20, 31)"],
*[style*="background-color: rgba(23, 20, 31"] {
    background-color: rgb(23,23,23) !important;
}

.non-selectable {
    background: linear-gradient(rgba(250, 250, 250, 0) 0%, rgb(32, 32, 32) 100%) !important;
}

/* change border-colors */
*[style*="solid rgb(32, 28, 43)"] {
   	border-color: rgb(32,32,32) !important;
}

*[style*="solid rgb(67, 63, 74)"] {
   	border-color: rgb(67,67,67) !important;
}

/* change icon colors */
.InputControls [class^="icon-"],
.InputControls [class*=" icon-"] {
    color: rgb(177, 177, 177) !important;
}

.icon-headerAddUser,
.icon-headerEvents,
.icon-headerSearch,
.icon-headerFiles,
.icon-headerChannels,
.icon-headerOverflow {
    color: rgb(137, 137, 137) !important;
}

div[style*="rgb(125, 91, 191)"] .icon-headerAddUser,
div[style*="rgb(125, 91, 191)"] .icon-headerEvents,
div[style*="rgb(125, 91, 191)"] .icon-headerSearch,
div[style*="rgb(125, 91, 191)"] .icon-headerFiles,
div[style*="rgb(125, 91, 191)"] .icon-headerChannels,
div[style*="rgb(125, 91, 191)"] .icon-headerOverflow {
    color: rgb(255, 255, 255) !important;
}

}
