css:
@-moz-document domain("vimeo.com")
{
  div[id^='clip_']
  { width: 100vw !important }

  div[id^='clip_'], .player_area, .js-player_area-wrapper
  { height: 56.25vw !important }

  .is_fullscreen div[id^='clip_']
  { height: 100% !important }

  .player_area
  { max-height: unset !important }
}
