body::-webkit-scrollbar, .nav-list::-webkit-scrollbar, .article-list::-webkit-scrollbar {
    width: 10px;
}
body::-webkit-scrollbar-track, .nav-list::-webkit-scrollbar-track, .article-list::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
}
body::-webkit-scrollbar-thumb, .nav-list::-webkit-scrollbar-thumb, .article-list::-webkit-scrollbar-thumb {
  background-color: #14191f;
  outline: 1px solid slategrey;
}
a.app-logo {
    display: none;
}
.header .btn-group-header .btn-header {
    padding: 3px 5px 3px 5px;
}
header.header {
    height: 30px;
}
.content-region {
    margin-top: 30px;
}
.section-header {
    top: 30px;
}
.article-pane-container .article-list-pane-container {
    top: 78px;
}
.nav {
    top: 30px;
}
.nav-list {
    padding-top: 0px;
}
.article-item-full .article-content, .article-item-view .article-content {
    max-width: 900px;
    margin-left: auto;
    margin-right: auto;
}
.article-item-full .article-body {
    margin-top: 15px;
    font-size: 16px;
}
body.theme-night .article-item-read .article-title {
    color: #cdd6e0;
    border-bottom: 1px solid #cdd6e0;
    padding-bottom: 3px;
}

}
