css:
::-webkit-scrollbar{height:13px;}
::-webkit-scrollbar{width:13px;}
::-webkit-scrollbar-track-piece:vertical{background-color:rgb(245, 245, 245);border: 1px solid rgb(211, 211, 211)}
::-webkit-scrollbar-track-piece:horizontal{background-color:rgb(245, 245, 245);border: 1px solid rgb(211, 211, 211)}

::-webkit-scrollbar-thumb:vertical{background-color:rgb(196, 196, 196);border-left: 1px solid rgb(211, 211, 211);box-shadow:inset 0 0 0 1px rgb(245, 245, 245), inset 1px 0 1px 1px rgb(176, 176, 176)}
::-webkit-scrollbar-thumb:vertical:hover{background-color:rgb(147, 147, 147);box-shadow:inset 0 0 0 1px rgb(245, 245, 245), inset 0 0 0 2px rgb(119, 119, 119)}
::-webkit-scrollbar-thumb:vertical:active{background-color:rgb(123, 123, 123);box-shadow:inset 0 0 0 1px rgb(245, 245, 245), inset 0 0 0 2px rgb(96, 96, 96)}

::-webkit-scrollbar-thumb:horizontal{background-color:rgb(196, 196, 196);border-top: 1px solid rgb(211, 211, 211);border-bottom: 1px solid rgb(211, 211, 211);box-shadow:inset 0 0 0 1px rgb(245, 245, 245), inset 1px 0 1px 1px rgb(176, 176, 176)}
::-webkit-scrollbar-thumb:horizontal:hover{background-color:rgb(147, 147, 147);box-shadow:inset 0 0 0 1px rgb(245, 245, 245), inset 0 0 0 2px rgb(119, 119, 119)}
::-webkit-scrollbar-thumb:horizontal:active{background-color:rgb(123, 123, 123);box-shadow:inset 0 0 0 1px rgb(245, 245, 245), inset 0 0 0 2px rgb(96, 96, 96)}
